package com.capgemini.coejava.basics;

public class ArithmeticOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int i = 10;
		int j = 20;
		System.out.println("add " + (i + j)); // adding and printing the result
		System.out.println("sub " + (i - j)); // subtracting and printing the result
		System.out.println("mul " + (i * j)); // multiplying and printing the result
		System.out.println("div " + (i / j)); // dividing and printing the result
		System.out.println("mod " + (i % j)); // modulus and printing the result
	}

}
