package com.capgemini.coejava.basics;

/**
 * @author ketan
 *
 */
public class RelationalOp {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=10; 
		int j=20;
		boolean k= (i<j);
		System.out.println(k);
		
		System.out.println(i==j);
		
		System.out.println(i!=j);
		
		System.out.println(i>j);
		
		System.out.println(i>=j);
		
		System.out.println(i<=j);
	}

}
