package com.capgemini.coejava.basics;

public class Variables {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		byte b = 108; //declaration and initialization of byte 
		short s = 10; //declaration and initialization of short
		int i = 101; //declaration and initialization of int
		long l = 1000l; //declaration and initialization of long
		float f = 1.9034567899f; //declaration and initialization of float
		double d = 2.67578654398908; //declaration and initialization of double
		char c = 'a'; //declaration and initialization of char
		boolean B = true ; //declaration and initialization of boolean
		
		System.out.println(b + "\n" + s );
		System.out.println(i + "\n" + l );
		System.out.println(f + "\n" + d );
		System.out.println(c + "\n" + B );
		

	}

}
